import React, {useState}  from "react";
import "./GameMatch.css";
import countryCapitalData from "./country-list.json";

export default function GameMatch() {
    const num = 5;
    const [gameEdn, setGameEnd] = useState(false);
    const [data, setData] = useState([]);
    
    function handleStart(){
        let ind = [], dataTemp = [];
        while (ind.length <= num) {
            let randNum = Math.floor((Math.random())*(countryCapitalData.length))
            if (ind.includes(randNum) === false) {
                ind.push(randNum);
                dataTemp.push(countryCapitalData[randNum])
            }
        }
        setData(dataTemp)
    }
    return (
        <div>
            <h1>Country Capital Matching Game!</h1>
            <button 
                onClick={()=> handleStart()}
                style={{display: gameEdn === false? "block" : "none"}} 
            >Start</button>
            <button 
                onClick={()=> handleStart()}
                style={{display: gameEdn === true? "block" : "none"}} 
            >Start Over</button>

        </div>
    )
}
