import React, {useEffect, useState} from "react"
import "./App.css"

export default function Calculater() {
    const [inputStr, setInputStr] = useState("");
    const [res, setRes] = useState(0);
    const specialInput = ["+", "-", "*", "/", "="];

    function handleInput(e) {
        setInputStr(e.target.value);
    }

    useEffect(()=> (strToExp()), [inputStr])

    function strToExp(strInput) {
        const arrInput = strInput.split();
        console.log(arrInput)
    }

    function handleSubmit(e) {
        if (specialInput.includes(inputStr[-1])) {
            setInputStr(inputStr.slice(0, -1));
        }
        var F=new Function (inputStr);
        setRes(F());

        e.preventDefault()
    }
    return (
        <div className = "wholeApp">
            <div className="panel">
                <div className="inputMath">{inputStr}</div>
                <div>={res}</div>
            </div>
            <form onSubmit={e => handleSubmit(e)}>
                <input onChange={e => handleInput(e)}></input>
                <button type="submit">Submit</button>
            </form>
        </div>
    )
}
