import React, {useState} from "react"
import "./App.css"

export default function Calculater() {
    const [inputStr, setInputStr] = useState("");
    const [res, setRes] = useState(0);
    const specialInput = ["+", "-", "*", "/", "="];

    function handleInput(e) {
        if (specialInput.includes(e.target.value)) {
            // console.log(inputStr)
            if (inputStr.length > 0) {
                if (specialInput.includes(inputStr[-1])) {
                    setInputStr(inputStr.slice(0, -1).repeat(1) + e.target.value);
                } else {
                    setInputStr(inputStr.concat(e.target.value));
                }
            }
        } else {
            console.log(inputStr)
            setInputStr(inputStr+e.target.value);
        }
    }

    function handleSubmit(e) {
        if (specialInput.includes(inputStr[-1])) {
            setInputStr(inputStr.slice(0, -1));
        }
        var F=new Function (inputStr);
        setRes(F());

        e.preventDefault()
    }
    return (
        <div className = "wholeApp">
            <div className="panel">
                <div className="inputMath">{inputStr}</div>
                <div>={res}</div>
            </div>
            <form onSubmit={e => handleSubmit(e)}>
                <input onChange={e => handleInput(e)}></input>
                <button type="submit">Submit</button>
            </form>
        </div>
    )
}
