import React, {useState, useEffect}  from "react";
import "./GameMatch.css";
import countryCapitalData from "./country-list.js";

export default function GameMatch() {
    const num = 5;
    const [gameEnd, setGameEnd] = useState(true);
    const [data, setData] = useState([]);
    const [matchedKey, setMatchedKey] = useState([]);
    
    function handleStart(){
        let ind = [], dataTemp = [];
        // select five country-caiptal pairs randomly
        while (ind.length < num) {
            let randNum = Math.floor((Math.random())*(countryCapitalData.length))
            if (ind.includes(randNum) === false) {
                ind.push(randNum);
                let key = Date.now();
                dataTemp.push([countryCapitalData[randNum].country, "country", key], [countryCapitalData[randNum].capital, "capital", key]);
            }
        }
        setGameEnd("false");
        // randomize matched buttons data
        let curr = dataTemp.length, rand;
        while (curr !== 0) {
            rand = Math.floor(Math.random() * curr);
            curr -=1;
            [dataTemp[curr], dataTemp[rand]] = [dataTemp[rand], dataTemp[curr]];
        }
        setData(dataTemp);
    }
    useEffect(()=>{
        if (data.length === 0) {
            setGameEnd("true")
        }
    }, [data])

    function handleMatchButton(eachItem) {
        return (
            <div >
                <button 
                    style = {{backgroundColor: 
                        (matchedKey.length === 1 && matchedKey[0][1] === eachItem[2]) ? "#0000ff": 
                        ((matchedKey.length === 2 && (matchedKey[0][1] === eachItem[2] || matchedKey[1][1] === eachItem[2]))? "#ff0000" : "none")}}
                    className="matchBtn" 
                    onClick={(e)=>handleClick(e, eachItem)}
                > {eachItem[0]} </button>
            </div>
        )
    }

    function handleClick(e, inputItem) {
        console.log(matchedKey, inputItem)
        if (matchedKey.length === 1) {
            if (inputItem[2] === matchedKey[0][1]) {
                let newdata = data.filter((i)=> {
                    return (i[2] !== matchedKey[0][1] && i[2] !== inputItem[2]) 
                })
                setData(newdata);
            } else {
                setMatchedKey(matchedKey.concat([inputItem[1], inputItem[2]]));
            }
        } else {
            setMatchedKey([inputItem[1], inputItem[2]]);
        }
        
        e.preventDefault();
    }
    return (
        <div>
            <h1>Country Capital Matching Game!</h1>
            {/* start and start over button */}
            <button 
                onClick={()=> handleStart()}
                style={{display: gameEnd === true? "block" : "none"}} 
            >Start</button>
            <button 
                onClick={()=> handleStart()}
                style={{display: gameEnd === false? "block" : "none"}} 
            >Start Over</button>
            {/* show matched buttons */}
            <div 
                style={{display: gameEnd === false? "grid" : "none"}} 
                className="container"
                >{data.map((item)=>handleMatchButton(item))}
            </div>
            <div style={{display: gameEnd === true? "block" : "none"}} >Congratulations!</div>

        </div>
    )
}
