import React, {useState, useEffect}  from "react";
import "./GameMatch.css";
import countryCapitalData from "./country-list.js";

export default function GameMatch() {
    const num = 5;
    const [gameEnd, setGameEnd] = useState(false);
    const [data, setData] = useState([]);
    const [matchedKey, setMatchedKey] = useState([]);
    let countryMap = new Map();
    let capitalMap = new Map();
    
    function handleStart(){
        let ind = [], dataTemp = [];
        // select five country-caiptal pairs randomly
        while (ind.length < num) {
            let randNum = Math.floor((Math.random())*(countryCapitalData.length))
            if (ind.includes(randNum) === false) {
                ind.push(randNum);
                let key = Date.now();
                countryMap.set(countryCapitalData[randNum].country, key);
                capitalMap.set(countryCapitalData[randNum].capital, key);

                dataTemp.push([countryCapitalData[randNum].country, "country", Date.now().Math.random()], [countryCapitalData[randNum].capital, "capital", Date.now().Math.random()]);
            }
        }
        setGameEnd(false);
        // randomize matched buttons data
        let curr = dataTemp.length, rand;
        while (curr !== 0) {
            rand = Math.floor(Math.random() * curr);
            curr -=1;
            [dataTemp[curr], dataTemp[rand]] = [dataTemp[rand], dataTemp[curr]];
        }
        setData(dataTemp);
    }
    useEffect(()=>{
        if (data.length === 0) {
            setGameEnd(true)
        }
    }, [data])

    function handleMatchButton(item) {
        return (
            <div >
                <button 
                    // style = {{backgroundColor: item[2] matchedKey? "#0000ff": "none"}}
                    className="matchBtn" onClick={(item)=>handleClick(item)}
                > {item[0]} </button>
            </div>
        )
    }

    function handleClick(item) {
        let isCountry = item[1];
        if (matchedKey.length === 0) {
            if (isCountry === "country") {
                setMatchedKey([countryMap.get(item[0]), item[2]]);
            } else {
                setMatchedKey([capitalMap.get(item[0]), item[2]]);
            }
        } else if (matchedKey.length === 1) {
            if (isCountry === "country") {
                if (countryMap.get(item[0]) === matchedKey[0][0] ) {
                    let newdata = data.filter((i)=> {
                        if (i[2] !== matchedKey[0][1] && i[2] !== item[2]) {
                            return i
                        }
                    })
                    setData(newdata);
                } else {
                    setMatchedKey(matchedKey.concat([countryMap.get(item[0]), item[2]]));
                }
            } else {
                if (capitalMap.get(item[0]) === matchedKey[0][0] ) {
                    let newdata = data.filter((i)=> {
                        if (i[2] !== matchedKey[0][1] && i[2] !== item[2]) {
                            return i
                        }
                    })
                    setData(newdata);
                } else {
                    setMatchedKey(matchedKey.concat([capitalMap.get(item[0]), item[2]]));
                }
            }
        } else {
            if (isCountry === "country") {
                setMatchedKey([countryMap.get(item[0]), item[2]]);
            } else {
                setMatchedKey([capitalMap.get(item[0]), item[2]]);
            }
        }
    }
    return (
        <div>
            <h1>Country Capital Matching Game!</h1>
            {/* start and start over button */}
            <button 
                onClick={()=> handleStart()}
                style={{display: gameEnd === false? "block" : "none"}} 
            >Start</button>
            <button 
                onClick={()=> handleStart()}
                style={{display: gameEnd === true? "block" : "none"}} 
            >Start Over</button>
            {/* show matched buttons */}
            <div className="container">
                {data.map((item)=>handleMatchButton(item))}
            </div>
            <div style={{display: gameEnd === true? "block" : "none"}} >Congratulations!</div>

        </div>
    )
}
